import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';

const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

/** @type {import('tailwindcss').Config} */
module.exports = {
  content: [
    "./src/**/*.{js,jsx,ts,tsx}",
  ],
  theme: {
    extend: {
      colors: {
        // Define primary colors based on Maleka Pharmacy branding (Emerald/Teal)
        emerald: {
          50: '#f0fdf4',
          100: '#dcfae7',
          600: '#059669',
          700: '#047857',
          800: '#065f46',
        },
        teal: {
          50: '#f0fdfa',
          600: '#0d9488',
        },
      }
    },
  },
  plugins: [
    require('@tailwindcss/line-clamp'),
  ],
}

